void mainImage(out vec4 o, vec2 O)
{
    float t = iTime*.5+1e3,
        a,
        A = 2.39,
        s,
        c;
    vec2 R = iResolution.xy,
        V=(O-.5*R)/R.y*4.5
        ,N=O/R-.5// normalized coords (0-1)
        ,P=O-R*.5// screen space coords, centered
        ;
    V.x-=.8;

   	//V*=mat2(c=cos(a=length(V)*.9-t*.2),s=sin(a),-s,c);
    //V*=mat2(c=cos(a=length(V)*.3+t*.5),s=sin(a),-s,c);

    a=(length(V)*(sin(t)*.3+.5))-t*.5;
    V*=mat2(c=cos(a),s=sin(a),-s,c);

    V=pow(abs(V),vec2(sin(t)*.5+1.));//1=dia, 2=cir

    o=N.xyxy+fract(V.x+vec4(N,-N)*.2+V.y);


    o.r*=.5;

    o+=.14*fract(sin(dot(R+t,N))*1e5)
        -dot(N,N*3.);

    o=max(o,vec4(.1));

    // some post
    if((abs(P.y) > R.x*.5/A) ||
       (abs(P.x) > R.y*.5*A))
        o-=o;
}
