vec4 colorA = vec4(52./255., 152./255., 219./255., 1.);
vec4 colorB = vec4(241./255., 196./255., 15./255., 1.);

float min_zoom = 3.5f;
float max_zoom = 19.5f;

float bars = 20.f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    float size = (sin(iTime)+1.)*((max_zoom-min_zoom)/2.)+min_zoom;
    float index = round(uv.x*size) + round(uv.y*size);
    float even = max(sign(mod(index, 2.)), 0.);

    fragColor = vec4(even > 0.? colorA : colorB );
}
