const float rings = 14.;
const float sections =4.;
const float raio = 0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2(cos(iTime),sin(iTime))*raio,
         dir = (fragCoord -.5* iResolution.xy) / iResolution.y - center;

    float ang = atan( dir.y, dir.x);
    float tdist =length(dir);
 float dist = tdist*rings;
    ang +=iTime;
    dist = dist +iTime;
    float c = sin(dist*3.1415) * sin((ang/2.)*sections) ;
    vec3 col =
        vec3(c,0,-c);

    fragColor = vec4(col,7);
}
