float sinm(float t, float vmin, float vmax)
{
	return (vmax-vmin)*0.5*sin(t)+vmin+(vmax-vmin)*0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
	float size = 0.15;
	float thickness = 0.0021;

	vec2 p = fragCoord.xy / iResolution.xx;
	vec2 c = vec2(0.5,0.5*iResolution.y/iResolution.x);

	float d = 0.0;
	float alpha = 0.15;
	for(int i=0 ; i<6 ; i++)
	{
		// vec2 dist = abs(p-c);
		float d1 = length(p-c);
		float d2 = abs(d1-size);
		d += thickness/d2;
		c += alpha*sign(p-c);
		size *= 0.40; //sinm(t,0.45,0.45);
		thickness *= 0.775;
		alpha *= 0.405;
	}

	fragColor = vec4(d*vec3(0.25,0.35,0.45),1.0);
}
