void mainImage( out vec4 O, in vec2 U )
{
	vec2 uv = U.xy / iResolution.xy;
    float blend = iMouse.x / iResolution.x;
    vec3 col = texture(iChannel0, uv).rgb;
    float dist = distance(uv, vec2(0.5)) + blend;
    float falloff = iMouse.y < 0.01 ? 0.1 : iMouse.y / iResolution.y,
    amount = iMouse.x < 0.01 ? 1.0 : blend; 
    col *= smoothstep(0.8, falloff * 0.8, dist * (amount + falloff));
    vec3 vst = texture(iChannel1, uv).rgb;
	O = vec4(mix(col, vst, blend), 1.0);
}
