#define T(x) texture(iChannel0, (x)/d)
#define C +T(T(u-y).zw*2.-1.+u+(y*=y.yx))/2.

void mainImage(out vec4 c, vec2 u)
{
    vec2 d = iResolution.xy, y = vec2(-.8,.8);

    c = mix(fract(sin((u/d).xxyy C) C C).gbar, T(u+y), .8);
}
