void mainImage( out vec4 c, vec2 f )
{
    vec4 g,p,s;

    f=(f-(c.xy = iResolution.xy)*.5)/c.y;

    c-=c;
    s-=s;
    float i,t=.1;

    for(i=.2;i<.6;i+=.001)
        p=vec4(f,sqrt(max(0., 1.-dot(f/i,f/i))),0.),
        g=texture(iChannel0, t*iTime+p.xy*.7/p.z,3.), 
        i > .59 ? s+=g,t=.2 : t, 
        g.g > i&&s.r==0. ? c = g : c;

    c+=max(s.b-5.,0.)/9.;
    c*=p.z;
}
