// [SH17A] Fractalicious
// @P_Malin
// https://www.shadertoy.com/view/MsBBzm

void mainImage( out vec4 c, vec2 f )
{
	f = f / iResolution.xy - .5;

	float l = 7e3, d;

	for(int i=0; i<50; i++)
	{
		f.x = abs(f.x);
		f = f * 1.2 + sin( iTime * vec2(.13,.05) ) - .5;			
        d = dot(f, f);
        f = .5 -f / d;		
		l = min(l, d);
	}

    c = sin(vec4(1, 2, 3,0)*iTime+d) + 1.;
	c = 1. - exp(-c * l * l * d * d * 15.);
}
