// please comment! I appreciate da input 
// http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/
// adapted from https://www.shadertoy.com/view/MdsGDr
// check out my tutorial http://corbinpercy.com/2d-fractals-part-2-fractal-tutorial-using-shadertoy/
// A little improv: https://www.shadertoy.com/view/MdjGRG    try it yourself!!

const int iterations=12;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 z=fragCoord.xy / iResolution.xy-.5;
	vec2 z = fragCoord.xy / iResolution.xy*2.0 - 1.0;

	//fixes aspect ratio in favor of symmety
	z.y*=iResolution.y/iResolution.x;

	//gloobywavez
	z.x += sin(z.y*1.0+iTime * .2)/10.0;

	//zooom
	z*= 1.2 + sin(iTime*.45);

	//pan
	z+=vec2(sin(iTime*.2),cos(iTime*.01));

	//rotate
	//z=vec2(z.x*cos(iTime*.2)-z.y*sin(iTime*.2),z.y*cos(iTime*.2)+z.x*sin(iTime*.2));

	//this is our "starting point" try numbers between 0 and 2 for both x and y 
	vec2 c=vec2(1.5, 1.1);

	float average=0.;
	float l=length(z);
	float prevl;
	for (int i=0; i<iterations; i++) 
	{	
		//kaliset base form
		z=abs(z)/dot(z,z) -c; 

		//this is another function that can be iterated to produce some different fractals
		//comment out the previous kaliset and experiment with values with this one!!
		//z = abs(z)/(z.x*z.y)-c;

		prevl=l;
		l=length(z);

		average+=abs(l-prevl);
	}

	//get the average length based upon the amount of iterations elapsed. multiply it to adjust "definition"
	average/=float(iterations) * 15.;

	//color fluctuation
	average+=iTime*0.02;

	vec3 myColor=vec3(0.2,0.21,.62);
	vec3 finalColor;	

	//set the colors! 
	finalColor.r = (fract(float(average)/myColor.r));
	finalColor.g = (fract(float(average)/myColor.g));
	finalColor.b = (fract(float(average)/myColor.b));

	fragColor = vec4(finalColor,1.0);
}
