void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = vec3(1.0,1.0,1.0);
    float time = iTime;

	for(int i = 20; i>0; i--)
    {     
        vec2 shape = uv - vec2(.5,.45);  
        shape.x+=cos(float(i)/3.5-time*1.3)/2.5;
        shape.y+=sin(float(i)/2.5-time*1.7)/2.5;

        float r = .1+.1*cos(atan(shape.y,shape.x)*12.);
        float g = .5+.5*cos(atan(shape.x,shape.y));   
        r=r*g-.02;    
        col *= smoothstep(r, r+0.05, length(shape));
        col.y+=.06;
    }
    fragColor = vec4(col,1.0);
}
