void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 p = uv - 0.5;

  vec4 c = vec4(uv,0.5+0.5*sin(iTime),1.0);

  float tt = iTime;

  float f = 1.2;
  p += vec2(sin(tt)*f, cos(tt)*f) * (sin(tt*0.6)*0.9);;

  float a = atan(p.y, p.x);
  float r = length(p);

  float s = 7.0;
  float tau = 2. * 3.1416;
  a = mod(a, tau/s);
  a = abs(a - tau/s/2.);

  c = c*a;
  a+=tt;

  p = r * vec2(cos(a),sin(a));

  vec4 color = texture(iChannel0, p + 0.5);
  fragColor = color + c;
}
