vec3 gamma(vec3 c)
{
    return pow(c,vec3(2.2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = uv.y;
    vec3 c3 = randomColor(floor(iTime*0.5)*3.); // Point A
    vec3 c2 = randomColor(floor(iTime*0.5)*3.+1.); // Point B
    vec3 c1 = randomColor(floor(iTime*0.5)*3.+2.); // Point C
    c1 = gamma(c1);
    c2 = gamma(c2);
    c3 = gamma(c3);
    vec3 ctrl = 2.*c2 - 0.5*(c1 + c3);
    vec3 ic1 = mix(c1,ctrl,t);
    vec3 ic2 = mix(ctrl,c3,t);
    vec3 col = mix(ic1,ic2,t);

	fragColor = vec4(pow(col,vec3(1.0/2.2)),1.0);
}
