void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
    uv -= vec2(0.5,iResolution.y/iResolution.x/2.0);

    vec3 bgColor = vec3(0.678);
    vec3 fgColor = vec3(0.325);

    float cellSize = 1.0/64.0;
    float cellBold = 0.08/64.0;

    vec3 color = bgColor;

    float i, j;

    int x = 0, y = 0;
    bool flag = false;

    // draw LCD bg
    for( i = -0.5; i <= 0.5; i+=cellSize )
    {
        for( j = -0.3; j <= 0.3; j+=cellSize )
        {
            if( uv.x >= i + cellBold && uv.x < i+cellSize-cellBold && 
                uv.y >= j + cellBold && uv.y < j+cellSize-cellBold )
            {
	            color = fgColor;
                flag = true;

                break;
            }

            y++;
        }

        if( flag )
            break;
        else
	        x++;
    }

    int time = int(iTime);

    // draw 
    if( ( x*y ) % (time % 20) == 0 )
        color = bgColor;

	fragColor = vec4(color,1.0);
}
