mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float random(vec2 st) { 
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

float map(vec3 p){
    p.xy*=rz2(iTime*.3);
    p.yz*=rz2(iTime*.5);
    p.xy*=rz2(iTime*.7);
    vec3 q=mod(p+2.5,5.)-2.5;
    vec3 f=floor((p-2.5)/5.);
    float fft=texture(iChannel0,vec2(0.05,0.25)).x;
    float r=random(vec2(random(f.xy),f.z));
    vec3 s=vec3(.2+r*.5+fft*.5);
    r*=6.2831853;
    vec3 c;
    c.x=sin(r+iTime*1.3);
    c.y=sin(r+iTime*1.4);
    c.z=sin(r+iTime*1.5);
    c*=.8;
    return min(length(max(abs(q-c)-s,0.))-.1,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 v=uv*(1.-uv);
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 ro=vec3(uv,-2.5),rd=vec3(uv,1.),mp=ro;
    int i;
    for(i=0;i<50;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
    float m=1.-float(i)/50.;
    vec3 c=vec3(.9,.1,.5)*m;
    c *= pow(v.x*v.y * 25., .25);
	fragColor = vec4(c,1.);
}
