void mainImage( out vec4 O, in vec2 U )
{
    U /= iResolution.xy;
    int c = int(3.*U.x);
    O-=O;

    do {
        switch(c) {
            case 0: O.r ++; 
            case 1: O.g ++; break; 
            case 2: O = vec4(0,0,1,0); return;
        }
    } while (O.r+O.g+O.b == 0.);    


	if (U.y<.5) O = vec4(0,1,0,0);     // this green should occurs only in the bottom-left-square (at most)
}
