//this function copied from https://www.shadertoy.com/view/4ljGD1
float getAmp(float frequency) { return texture(iChannel0, vec2(frequency / 512.0, 0)).x; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color1 =  vec3(0.5,0.1,0.7);
    vec3 color2 =  vec3(0.3,0.35,0.8);

	fragColor = vec4(mix(color1,color2,uv.x  * getAmp(uv.x) * 5.5 ),1.0);
}
