#define PI 3.14159
#define SPEED .5
#define SCALE (4.)

#define atan2(a) atan(a.x,a.y)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime*SPEED*4./(2.*PI))*2.*PI;
	vec2 uv = fragCoord.xy / iResolution.xy, muv = uv;
    uv *= SCALE;
    uv.y/=iResolution.x/iResolution.y;
    uv = fract(uv);
    uv -= .5;
    uv *= 3.;
    float s = sin(t), c = cos(t);
    vec2 cpos = vec2(s,c)*1.4;
    float color = length(uv-cpos) < .12 || length(uv+cpos) < .12 ? 1. : 0.;

    muv.y/=iResolution.x/iResolution.y;
    muv *= SCALE;
    muv +=.5;
    muv = fract(muv);
    muv -=.5;

    float ang = t+.2*cos(t*4.+1.7);

    s = sin(ang), c = cos(ang);

    muv *= mat2(c,s,-s,c);
    float color2 = 0.;
    if(iMouse.w < 0.1)
    color2 = length(muv) < .45 ?
        		   pow(abs(sin(atan2(muv)*2.)),.1) > .9 ? 1.: 0. : 0.;
    fragColor = vec4(color,0,0,1)+color2;
}
