void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float m =500.;
    vec3 c0 = texture(iChannel0, floor(uv*m)/m).rgb;
    vec3 c1 = texture(iChannel1, floor(uv*m)/m).rgb;
    float x = iTime/10.;
	fragColor = vec4(mix(c0,c1,fract((uv.x+uv.y)*m)),1);
}
