//By Nestor Vina

#define j  + texture(iChannel0,(p.xy + p.z * vec2(2,3) + iTime)*.01*++m).r*.2/m
void mainImage( out vec4 c, vec2 f )
{    
    c-=c;
    float i = 1.,m,a;
    while(i++ < 9.){
        vec3 p = (vec3(f/iResolution.y,1)-.5)*i + vec3(0,0,-3);    
        m=0.; a = pow(j j j j j j,length(p)*2.);            
        c.rgb += a*sin(p+iTime*vec3(.1,.2,.7))*.5+a*a*2.;
    }      
}
