void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float skip_ahead_seconds = 240.;

	vec2 uv = ((skip_ahead_seconds + iTime / 20.0) / 1.0) * ((fragCoord.xy - (vec2(iResolution.x,0))) / iResolution.y);

    //uv = floor(uv * 2.);
    float value = (uv.y) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    color = color * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(med*2., high, med,1.0);
    } else {
      fragColor = vec4(high, med, med*2.,1.0);
    }
}
