void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / -iResolution.xy;
    vec4 sum = vec4(0.0);
    vec2 scale = vec2(iMouse.w, iMouse.w) / iResolution.xy;
    float maxsamples = iMouse.z / 20.0;
    float anyad = 0.0;

    if(maxsamples >= 30.0)
    {
     	maxsamples = 29.0;   
    }
    else if(maxsamples < 1.0)
    {
        maxsamples = 1.0;  
    }

    for(float x = 0.5; x < maxsamples; x++)
    {
        float newx = x / maxsamples; 
    	float weight = 1.0 - newx;
    	sum += texture(iChannel0, uv + (vec2(x, x) * scale) / maxsamples) * weight;
        sum += texture(iChannel0, uv + (vec2(-x, -x) * scale) / maxsamples) * weight;
        anyad += 2.0 * weight;
    }


	fragColor = sum / anyad;
}

