#define PSIN(x) (.5*sin(x)+.5)

const float M_PI = 3.1415926535897932384626433;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // initialize
    fragColor = vec4(0,0,0,1);

    vec2 R = iResolution.xy;
	vec2 uv = (fragCoord - .5*R)/R.y;

    // declaration
    const float size = 0.3;
    const float brightness = 0.002;
    const float cycle = 100.0;

    // draw
    for(int i=0;i<63;++i){
        float t = iTime + float(i) *0.1;

        vec2 p = size * vec2(cos(t), sin(t)) * PSIN(t* (sin(t/cycle)+8.0) );

        float b = brightness / length(uv - p);
        fragColor += vec4( b * PSIN(t+vec3(0,1,-1)*M_PI*3./2.), 1.0);
    }
}
