void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 even  = texture( iChannel1, uv * vec2(0.5,1.0));
    vec4 odd  = texture( iChannel2, uv * vec2(0.5,1.0) +  vec2(-0.5 / iResolution.x,0.0)  );

       if ( mod( fragCoord.x + 0.5, 2.0 ) == 0.0 )
        {
               fragColor = even;
        }
        else
        {

                fragColor = odd;
        }
}

float sphere( vec3 spherepos, float r, vec3 raypos )
{
    float disp = texture( iChannel2, raypos.xy * 0.05 ).r;
    return distance( spherepos, raypos ) - r - disp*2.0;
}

float cylinder( float r, vec3 raypos )
{
    return r - length( raypos ) ;
}

float plane( vec4 plane, vec3 raypos )
{

    float scale = max( 0.0, 1.0 -  length( raypos.xz) / 40.0 );

    float d = cos( iTime * 10.0 - distance( vec2( 0,0) , raypos.xz) * 0.5 ) * 2.0 + 
        	  cos( iTime * 10.0 - distance( vec2( 10.0,5.0) , raypos.xz) * 1.0 )  * 0.8 + 
         	  cos( iTime * 10.0 - distance( vec2( -10.0,-5.0) , raypos.xz) * 2.0 ) * 0.8;

    float disp = texture( iChannel1, raypos.xz * 0.01 ).r;

    return dot( raypos, plane.xyz ) - plane.w - d*scale;
}



vec4 getlight( vec3 eye, vec3 normal, vec3 position, vec3 lightpos, vec4 lightcolour  )
{
    float d = distance( lightpos, position );
    vec3 n = normalize( lightpos - position  );

    vec4 spec = pow( max( dot( normalize( n - eye ), normal ), 0.0 ), 16.0 ) * vec4(1.0,1.0,1.0,1.0);


    return max( dot( normal, n ), 0.0 ) * lightcolour + spec * 0.1;


}


float softmin( float a, float b, float d )
{
    if (abs( a-b ) > d )
    {
        return min( a, b );
    }
   	else
    {
        float lerp = abs( a - b ) / d;
        return min(a,b) * lerp +  (a+b)/2.0 * ( 1.0 - lerp );

    }
}

float sdf( vec3 raypos )
{
    float e = abs( cos( iTime ) ) * 10.0;
    return min( sphere( vec3( 0,5.0, 0.0), 10.0, raypos ),
                 //min( cylinder( 200.0 ,raypos ),
                 plane( vec4( 0,1.0, 0.0,-10.0), raypos ));
}


vec3 grad( vec3 raypos, float delta )
{
    float dx =  sdf( raypos + vec3( delta, 0,0 ) ) - sdf( raypos - vec3( delta,0,0 ) );
    float dy =  sdf( raypos + vec3( 0, delta,0 ) ) - sdf( raypos - vec3( 0,delta,0 ) );
    float dz =  sdf( raypos + vec3( 0,0, delta ) ) - sdf( raypos - vec3( 0,0,delta ) );
    return vec3( dx,dy,dz );
}

vec4 shadowlight( vec3 eye, vec3 normal, vec3 origin,  vec3 lightpos, vec4 colour, float ep )
{
   vec3 ray = normalize( lightpos - origin);

    vec3 p = origin + ray * 2.0;
    for ( int i = 0; i < 512; i++ )
    {
        float sd = sdf(p);
        float ld = distance( p, lightpos );
        float step = min( sd, ld );
        if ( sd  <  ep )
        {
           return vec4(0,0,0,1.0);
        }
        else if ( ld < ep )
        {
           return getlight( eye, normal, origin, lightpos, colour );
        }

        p += ray * step;
    } 


    return getlight( eye, normal, origin, lightpos, colour );
}

vec4 contact( vec3 position, vec3 normal, float scale )
{
    float s = sdf( position + normal * scale );
    return  (s/scale) * vec4( 1.0,1.0,1.0,1.0);

}

vec4 march( vec3 ray, vec3 origin, float ep )
{
    vec3 p = origin;
    vec3 eye = normalize( ray );
    for ( int i = 0; i < 512; i++ )
    {
        float step = sdf(p);
        if ( step  <  ep )
        {
            vec3 normal = normalize( grad( p, 0.1 ) );
            vec4 cube =  texture( iChannel0,  -reflect( eye, normal ) ); //reflect( eye, normal ) );
 //           return //shadowlight( eye, normal, p,  vec3( 100,100,100 ), vec4(0.5,0.5,0,1.0 ), ep) + 
   //                shadowlight( eye, normal, p,  vec3( -100,100,-100 ), vec4(0.0,0.5,0.5,1.0 ), ep) + 
     //         contact( p, normal, 4.0 ) * 0.1;
            return cube *  contact( p, normal, 4.0 );
        }
        else if ( length( p - origin ) > 200.0 )
        {
           return texture( iChannel0,  -eye );
        }

        p += ray * step;
    }

    return vec4(0.0,0,0,1);
}

vec3 rotatevecY( vec3 vec, float angle )
{
    vec3 m0 = vec3( -cos( angle ), 0, sin( angle ));
    vec3 m1 = vec3( 0            , 1.0,   0      );
    vec3 m2 = vec3( sin( angle ), 0, cos( angle ) );

    return vec3(  dot( m0, vec ), dot( m1, vec ), dot( m2, vec )) ;
} 


void shade( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float aspect = iResolution.y / iResolution.x;

    vec3 origin = vec3(0.0, 2, -60.0 + iMouse.y / iResolution.y * 100.0);
    vec3 ray = vec3( uv.x - 0.5, (uv.y - 0.5) * aspect, 0.5 );



    ray = rotatevecY( ray, iMouse.x / iResolution.x * 8.0 );
    origin = rotatevecY( origin,  iMouse.x / iResolution.x * 8.0  );


	fragColor = march( ray, origin, 0.1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  scale = 1.0 / iResolution.xy;
    vec2 uv = fragCoord * scale;
    //vec4 t = texture( iChannel0, uv );
    vec4 old = texture( iChannel1, uv );


    if( mod( float(iFrame), 2.0 ) != 0.0 || uv.x > 0.5)
    {

        fragColor = old;
    	return;
    }


     shade( fragColor, ( fragCoord + vec2(0.0,0.0) ) * vec2(2.0,1.0) );

}

