const vec3 color = vec3(227.0 / 255.0, 234.0 / 255.0, 242.0 / 255.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

	vec3 c = color;    

    float a = clamp(abs(sin(iTime * 2.0) + 1.6), 0.0, 1.0);
    c += a * (sin(uv.x * 20.0) + 1.0) / 10.0;

    fragColor = vec4(c,1.0);
}
