void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //how many cells on the grid
	float cells = 13.0;  
    //Line thickness (this need to be made better)
    float linethickness = 0.000035;

    //get uv's coordinate
    vec2 uv = fragCoord/iResolution.xy;

    //get plane's proportion
    float proportion = iResolution.x/iResolution.y;

    //calculate line thickness
	vec2 thickness = iResolution.xy*linethickness;

    //initialize color value as white.
    vec3 col = vec3(1,1,1);

    //if the pixels near the line are about to be drawn, paint them black.
    if(fract(uv.y*cells)<thickness.x || fract(uv.x*cells*proportion)<thickness.y*proportion) 
    	col = vec3(0,0,0);

    //return color
    fragColor = vec4(col,1.0);
}
