struct Objekt {
    vec2 pos,
    	 width,
         height;
    vec4 col;
     };

Objekt k1 = Objekt (vec2 (- cos(.5*iTime),0.),vec2 (0.2,.0), vec2 (0.,0.2), vec4(1.,0.8,.25,1.)),
 	   k2 = Objekt (vec2 (1. -sin(.5*iTime),0.),vec2 (0.4,.0), vec2 (0.,0.4), vec4(0.,1.,.7,1.));


vec4 Hitkreis (in vec2 p_uv, in Objekt p_k){

        vec2 xynew = p_uv - p_k.pos;
        float
        x = dot(xynew, p_k.width)/dot(p_k.width, p_k.width),
        y = dot(xynew, p_k.height)/dot(p_k.height, p_k.height); 

    if (abs(x)>.5 && abs(y) >.5)
    { 
        return vec4(.0);
    }
    if (x*x+y*y<.25)
        return p_k.col;

    return vec4(.0);
} 

bool kollision (in vec2 p_x1 ,in vec2 p_x2) {
    if (abs(p_x1.x- p_x2.x) >=0. && abs(p_x1.x-p_x2.x) <=.25){
        return true;
            }
    return false;
        }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
		vec2 uv = 2.*(fragCoord.xy / iResolution.xy-0.5)*vec2 ( iResolution.x/iResolution.y, 1.);
	fragColor = vec4(0.2,.5,.65,1.0);


     if( kollision( k1.pos , k2.pos ) )
        k1.pos.x = -cos(iTime+(2.*iTime-3.14)),
        k2.pos.x = -k1.pos.x;

     vec4 col = Hitkreis( uv, k1)+ Hitkreis(uv, k2);

    if (col.a < .9)
        fragColor = vec4(0.9,.5,.0,1.0);

    else
    fragColor = col;

    vec4 col2 = Hitkreis (uv,k1)+Hitkreis(uv,k2);





}
