void mainImage( out vec4 f, in vec2 g )
{
    f = vec4(.8,.2,.5,1);

    vec2 s = iResolution.xy;
    vec2 uv = g/s;


    if (uv.y<0.01){
      uv.x=abs(2.0*(uv.x-0.5));
      vec4 v = texture(iChannel1, vec2(exp(-uv.x*3.0),0.29));      
      float fft=(v[0]*v[0])*1.5;
      f = vec4( sin(fft*3.141*2.5), sin(fft*3.141*2.0),sin(fft*3.141*1.0),1.0);

    }else{
      float b=0.0005+0.025 *texture(iChannel0, vec2(uv.x,0.0))[0];
      vec4 h = texture(iChannel0, vec2(uv.x,uv.y-b));
      f=h;
    }


}
