vec2 multiply(vec2 a, vec2 b) {
	return vec2(a.x*b.x - a.y*b.y, a.x*b.y+a.y*b.x);
}

float hash(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise(vec2 coord) {
	vec2 w = floor(coord),
         f = fract(coord);

    f = f*f*(3.0 - 2.0*f);

    return mix( mix( hash(w+vec2(0, 0)), hash(w+vec2(1, 0)), f.x ),
                mix( hash(w+vec2(0, 1)), hash(w+vec2(1, 1)), f.x ), f.y );
} 

float fbm(vec2 coord) {
    float _fbm = 0.0;
    _fbm += .5000*noise(2.*coord) +
    		.2500*noise(4.*coord) +
    		.1250*noise(8.*coord) +
        	.0625*noise(16.*coord);
	return _fbm;
}

void mainImage( out vec4 col, in vec2 fc )
{
	vec2 uv = fc.xy / iResolution.xy;
    uv = uv*2.0 - 1.0;
	uv.x *= iResolution.x/iResolution.y;
	uv *= 1.67;
    vec2 z_prev = vec2(0.0);

    // update: animation
    uv *= 3.4*fbm((.4*iTime)*fc.xy / iResolution.xy);

    for(int i=0; i<4; i++) {
        z_prev += multiply(z_prev,
                          multiply(z_prev, 
                                   multiply(z_prev,
                                                multiply(z_prev, z_prev)))) + multiply(uv, uv);
        uv /= multiply(uv, uv);
    }
    col.rg = multiply(z_prev, z_prev);
    col.b  = 1.*length(multiply(z_prev, multiply(z_prev, multiply(z_prev, multiply(z_prev, multiply(z_prev, z_prev))))));
    col.r = abs(col.r);
}
