void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy; //Condensing coordinates from 0 to 1
    vec4 texColor = texture(iChannel0, xy);
    float aveValue = (texColor.r + texColor.g + texColor.b) / 3.0;
    float timer = sin(iTime);
    //texColor.b = aveValue;
    //texColor.g = aveValue;
    //texColor.r = aveValue;
    vec4 greyscale = vec4(aveValue, aveValue, aveValue, 1.0);
    //fragColor = texColor;
    vec4 displayColor = texColor*timer + greyscale*(1.0-timer);
    fragColor = displayColor;
}
