#ifdef GL_ES
precision highp float;
#endif

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy/iResolution.xy;    

    float speed = 2.;

    float a = -iTime * speed + uv.x*12. + uv.y*1.;

	float x1 = uv.x + sin(a)*10./iResolution.x;
    float y1 = uv.y + cos(a)*10./iResolution.y;

    vec4 c1 = texture(iChannel0, vec2(x1,y1));

	fragColor = vec4(c1.xyz, 1.);
}
