void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(gl_FragCoord.x / iResolution.x, gl_FragCoord.y / iResolution.y); 
  	uv -= .5;
  	uv /= vec2(iResolution.y / iResolution.x, 1.);

  	vec3 col = vec3(0);
  	float t = iTime*2. + texture(iChannel1, vec2(0.02)).x*1.5 - texture(iChannel3, vec2(0.04)).x*3.;

  	for (float j = 0.; j < 140.; j++) {
    	vec3 pointCol = vec3(10,2,0.3);
    	float i = j;

    	float star = 1.;
    	float starPos = 0.;
    	if (i > 70.) {
      		pointCol = vec3(0.2,4,8);
      		i += 234.;
      		star *= texture(iChannel0, vec2(0.05)).x;
      		starPos = texture(iChannel2, vec2(0.03)).x*10. - texture(iChannel3, vec2(0.04)).x*20.;
    	} else {
      		star *= texture(iChannel0, vec2(0.08)).x*1.4;
      		starPos = texture(iChannel2, vec2(0.035)).x*15. - texture(iChannel1, vec2(0.025)).x*10.;
    	}

    	vec2 p = vec2(sin(i*0.1423 + t*0.1) + cos(i*0.17 + t*0.21), sin(i*0.0731 + t*-0.14) + cos(i*.17 + t*0.221)) * 0.4;

    	vec2 diff = p - uv;

    	float a = atan(diff.y,diff.x);

    	float highlight = 1000.*pow(0.5+0.5*sin(i*0.1 - iTime*0.5 + starPos*0.4), 300.);

    	float pointB = 1. + highlight * (0.1 + pow(0.5+0.5*sin(a*12.), 10.)) * star * 10.;

    	float dist = length(diff);

    	float brightness = pow(0.1/(0.0001 + dist), 2.) * pointB;

    	for (float f = 0.; f < 3.; f++) {
      		vec2 p2 = -p * (0.4 + f * 0.3);
      		float diff2 = distance(uv,p2) * 1.5;
      		float flare = smoothstep(0.14,0.13,diff2) * (smoothstep(0.1,0.11,diff2) * 0.1+0.1);
      		col += pointCol * flare * pointCol * 0.00001 * highlight;
    	}
    	col += pointCol * brightness * 0.0001;

  	}

  	col /= (vec3(1)+col);

  	fragColor.rgb = pow(col, 1./vec3(2.2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, vec2(0.02));

    float delta = texture(iChannel1, vec2(0.0)).x;

    fragColor += vec4(pow(delta/1.,4.)/10.);
}

