void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 stretch = vec2(sin(iTime*5.+uv.x*50.)*0.01, cos(iTime*5.+uv.y*50.)*0.01);
    uv += stretch;
	float aspect = iResolution.x/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 vecDiff = vec2(uv.x*aspect-mouse.x*aspect, uv.y-mouse.y);
    float radius = 0.2;
    float d = length(vecDiff);
    float angle = atan(vecDiff.y, vecDiff.x);
    float res = 0.005;//0.0*sin(iTime)*sin(iTime);
    float frills = 1.+sin(angle*10.+iTime)*5.2;
    res+=frills*0.01;
    d = smoothstep(max(radius-res,0.), max(radius+res, 0.), d);
    vec3 col = vec3(d, d, d);
    if(d<radius) col = vec3(1.-d, 1.-d, 0);
    // Output to screen
    fragColor = vec4(col,1.0);
}
