#define pi (acos(-1.))

float noise(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.43243,4.543))*46573.71254));
}

vec3 baseColor(vec2 uv, float t)
{
    float f = texture(iChannel0, vec2(0.005,0.)).r*0.5;
    float o = texture(iChannel0, vec2(mod((t+f)/10.+(uv.x/(pi)+1.)/2.,1.),0.)).r*0.5;
    float xDist = f*o;
    vec3 col1 = cos(100.*uv.y*o+t+f*o)*(0.5 + 0.5*cos(-t+uv.xyx+vec3(0,2,4)));

    vec3 col2 = 0.5 + 0.5*cos(t+uv.xyx+vec3(0,2,4));
    return mix(col1, col2, -(5.*(uv.y-o)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x *= iResolution.x/iResolution.y;    

    vec2 puv = vec2(atan(uv.y,uv.x), length(uv));

    float t = iTime + texture(iChannel0, vec2(0.005,0.)).r*0.5;

    // Time varying pixel color
    vec3 col = baseColor(puv, iTime);

    // Output to screen
    fragColor = vec4(col,1.0);
}
