float sphere (vec3 r, vec3 o, float radius){
    return length(o-r)-radius;
}
vec4 trace (vec3 o, vec3 r, vec3 pos, float radius){
    float t = 0.0;
    vec3 p;
    for(int i = 0; i<32; ++i){
        p = o+r*t;
        float d = sphere(p, pos, radius);
        t += d*0.5;
    }
    return vec4(p, t);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse-=0.5;
    vec3 camera = vec3(0., 0., -2.)-vec3(mouse, 0.)*3.;
    vec3 ray = vec3(uv.x, uv.y, 0.)-camera;

    float dist = 10000.;
    int tex = -1;
    vec2 finalXY = vec2(0.);

    float radius = -1.;
    vec3 spherePos = vec3(-1.);
    vec4 d = vec4(-1.);
    float l = -1.;

    vec2 rotPos = vec2(sin(iTime), cos(iTime)+1.);//a standard rotation point 

    //for each sphere BEGIN
    radius = 0.8;
    spherePos = vec3(rotPos.x, 0., rotPos.y+radius*1.5);
    d = trace(camera, ray, spherePos, radius);
    l = length(d.xyz-spherePos);
    if (abs(l-radius)<0.05){
        dist = d.w;
        float anglex = atan(d.z-spherePos.z, d.x-spherePos.x)-iTime;
        float angley = (d.y-spherePos.y)/radius;//not really an angle
        finalXY = vec2(anglex, angley);
        tex = 0;
    }
    //END

    //some more spheres VVVVV
    float pr = radius;//the previous sphere's radius
    radius = 0.5;
    spherePos = vec3(rotPos.x-sin(iTime*1.3)*pr, 0., rotPos.y+pr*1.5-cos(iTime*1.3));
    d = trace(camera, ray, spherePos, radius);
    l = length(d.xyz-spherePos);
    if (abs(l-radius)<0.05&&d.w<dist){
        dist = d.w;
        float anglex = atan(d.z-spherePos.z, d.x-spherePos.x)-iTime;
        float angley = (d.y-spherePos.y)/radius;//not really an angle
        finalXY = vec2(anglex, angley);
        tex = 1;
    }

    radius = pr*0.5;
    spherePos = vec3(rotPos.x-sin(iTime*1.3)*pr, 0., rotPos.y+pr*1.5-cos(iTime*1.3));
    d = trace(camera, ray, spherePos, radius);
    l = length(d.xyz-spherePos);
    if (abs(l-radius)<0.05&&d.w<dist){
        dist = d.w;
        float anglex = atan(d.z-spherePos.z, d.x-spherePos.x)-iTime;
        float angley = (d.y-spherePos.y)/radius;//not really an angle
        finalXY = vec2(anglex, angley);
        tex = 1;
    }
    //end of more spheres

    float fog = 1.0 / (1.0 + dist*dist*0.3);
    vec2 xy = finalXY;
    vec4 pixColor = vec4(1.);
    if(tex==0){
    	pixColor = texture(iChannel0, xy);
    } else if (tex==1){
        pixColor = vec4(texture(iChannel1, xy).rrr, 1.);//.rr cause it only has one channel, ya na?
    } else if (tex==2){
        pixColor = texture(iChannel2, xy);
    } else if (tex==3){
        pixColor = texture(iChannel3, xy);
    }

    float mixValue = sin(iTime*0.4)*0.5+0.5;
    vec4 altTex = texture(iChannel3, xy);
    pixColor = pixColor*(1.-mixValue)+altTex*mixValue;
    pixColor *= fog;
	vec3 col = pixColor.rgb;
    fragColor = vec4(col,1.0);
}
