void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/iResolution.y;

    float pixel_radius = 43.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-= 0.5;
    uv.x *= aspect;
    uv+= 0.5;

   	float circle_radius = pixel_radius / iResolution.y;
    vec2 circle_center = vec2(0.5, 0.5);

  //  float  dist = smoothstep(circle_radius, 0.0, distance(uv, circle_center));
    float  dist = step(circle_radius, distance(uv, circle_center));

    vec4 innerColor = vec4(1,0,0,1);
    vec4 outerColor = vec4(1,1,1,1);

    fragColor = mix(outerColor, innerColor, dist );
    fragColor = sqrt(fragColor);
}
