void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / (iResolution.xy);
    vec4 color = texture(iChannel1, uv);
    vec2 uz = vec2(fragCoord.x + iTime * 100000.0,fragCoord.y + iTime * -10000.0) / vec2(iResolution.x / 1.0,iResolution.y / 1.0);
    vec4 grain = texture(iChannel0, uz) * 0.2 + 0.8;
    vec4 crtScan = vec4(clamp(mod(uv.y * iTime + 1.5,20.0),0.0,1.0));
    vec4 scanlines = clamp(vec4(mod((uv.y * 200.0), 2.4)), 0.2, 1.0) * 0.4 + 0.6;
    vec4 effect = (grain * scanlines); //* crtScan;
    fragColor = effect * color * 1.4;
}
