void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;
    xy -= 0.5;									   // center on screen

    float func = xy.y - (xy.x*xy.x);               // level sets
    float func2 = xy.y;
    func = mix (func, func2, cos(2.*iTime)); // interpolate between funcs

    fragColor = texture( iChannel0, vec2(xy.x, func) + .5 );
}
