// from discussions in "Terrain Traveler"  https://shadertoy.com/view/ldKyWc

vec4 color1 = vec4(0,1,1,0)*.3,            // ambiant color
     color2 = vec4(1,0,0,0);               // highlighted color

float wrap = .5,
      shape = 2.;

void mainImage( out vec4 O, vec2 U )
{

    vec2 R = iResolution.xy, 
         M = iMouse.xy/R;
         U = ( U + U - R ) / R.y; 
    O -= O;

    float l = length(U),
      dotNL = sqrt(1.-l*l),                // N = ( U.x, U.y, sqrt(1.-l*l) )
          a = 1.57* sin(iTime);
    if ( M != vec2(0) ) wrap = M.x, shape = 4.*M.y;

    dotNL = dot(vec3(U,dotNL), vec3(sin(a),0,cos(a)) ); // comment for L = (0,0,1)
    O = color1 + (color2-color1)*pow( clamp( mix(dotNL,1.,wrap), 0.,1.), shape );
      // = mix( color1, color2, pow() )

    O += smoothstep(.0003,0.,1.-dotNL);    // light dir
    O -= smoothstep(.01,0.,abs(dotNL));    // shadow silhouette
    O *= smoothstep(0.,3./R.y,1.-l);       // sphere antialiasing
    O = pow(O,vec4(1./2.2));               // to sRGB
}
