float ringWidth = 0.45;
vec3 ringColor = vec3(1.0,0.2,0.7);

vec4 outline(float width, vec2 tc, vec3 color, sampler2D tex){
    vec4 t = texture(tex, tc);
    tc -= 0.5;
    tc.x *= iResolution.x / iResolution.y;

    float grad = length(tc);
    float circle = smoothstep(0.5, 0.49, grad);
    float ring = circle - smoothstep(width, width-0.005, grad);

    t = (t * (circle - ring));
    t.rgb += (ring * color);

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 t = outline(ringWidth, uv, ringColor, iChannel0);

    fragColor = t;
}
