#define GAMMA true // Toggle between true<->false

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Gamma correction algorithm
    vec3 color = pow(texture(iChannel0, uv).xyz, vec3(2.2));

    // Output to screen
    if (GAMMA) fragColor = vec4(pow(color, vec3(2.2)), 1.0);
    else fragColor = vec4(pow(color, vec3(1.0/2.2)), 1.0);
}
