void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.0, 0.0, 0.0);
    float sqrt3 = 1.73205;
    r *= 2.5;

    float the = 0.0 + iTime * 1.0;
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec2 r1 = abs(r);
    r1.y += 1.0 * r1.x / sqrt3;
    if(r1.x <= 0.5 *  sqrt3 && r1.y  <= 1.0){
    	ret = vec3(1.0);
    }
    vec2 r2 = abs(r - vec2(sqrt3 * 0.5, -1.5));
    r2.y += 1.0 * r2.x / sqrt3;
    if(r2.x <= 0.5 *  sqrt3 && r2.y  <= 1.0){
    	ret = vec3(1.0, 0.0, 0.0);
    }
    vec2 r3 = abs(r - vec2(sqrt3 * 0.5, 1.5));
    r3.y += 1.0 * r3.x / sqrt3;
    if(r3.x <= 0.5 *  sqrt3 && r3.y  <= 1.0){
    	ret = vec3(0.0, 1.0, 0.0);
    }
    vec2 r4 = abs(r - vec2(sqrt3, 0.0));
    r4.y += 1.0 * r4.x / sqrt3;
    if(r4.x <= 0.5 *  sqrt3 && r4.y  <= 1.0){
    	ret = vec3(1.0, 1.0, 0.0);
    }
    vec2 r5 = abs(r - vec2(-sqrt3, 0.0));
    r5.y += 1.0 * r5.x / sqrt3;
    if(r5.x <= 0.5 *  sqrt3 && r5.y  <= 1.0){
    	ret = vec3(0.0, 0.0, 1.0);
    }
    vec2 r6 = abs(r - vec2(-sqrt3 * 0.5, 1.5));
    r6.y += 1.0 * r6.x / sqrt3;
    if(r6.x <= 0.5 *  sqrt3 && r6.y  <= 1.0){
    	ret = vec3(0.0, 1.0, 1.0);
    }
    vec2 r7 = abs(r - vec2(-sqrt3 * 0.5, -1.5));
    r7.y += 1.0 * r7.x / sqrt3;
    if(r7.x <= 0.5 *  sqrt3 && r7.y  <= 1.0){
    	ret = vec3(1.0, 0.0, 1.0);
    }

	fragColor = vec4(ret,1.0);
}
