#define RADIUS .025
#define STEP 2.
#define BLUE vec3(.11, .15, .2)
#define WHITE vec4(1., .98, .9, 1.)
#define RED vec3(.6, .09, .08)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
	vec2 pos = (fragCoord -.5 * lM) / lM.x;

    pos.x += RADIUS;

    fragColor = WHITE;

    if (abs(pos.x - RADIUS) > STEP * RADIUS * 3. || abs(pos.y) > RADIUS * 3.) return;

    float x = mod(iTime * 4. + floor((pos.x / RADIUS) / 2.), 12.);
    float size = -.1 * x * x + x;
    size /= 2.5; // -b / 2 * a

    if (size < 0.) return;

    pos.x = mod(pos.x, RADIUS * 2. * STEP) - RADIUS;

    float dist = length(pos);
    float radius = RADIUS * size;
    if (dist < radius)
        fragColor = mix(WHITE, vec4(mix(RED, BLUE, size), 1.), smoothstep(radius, radius * .93, dist));
}
