void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalisierte Bildkoordinaten
    vec2 uv = fragCoord / iResolution.xy;

    // Skalierung des Bildes - auf niedrigeren Wert ändern, um die Randbehandlung zu sehen
    float scale = 1.0;

    // Zugriff auf das mit iChannel0 verknüpfte Bild
    vec3 col = texture(iChannel0, uv / scale).rgb;

    // Neuer Farbwert
    fragColor = vec4(col, 1.0);
}
