// replace length(U) by max(U.x,U.y) for squares
// U = abs( (U*2.5-1.) *mat2(cos( iTime + vec4(0,33,55,0)))) for rotation 

void mainImage(out vec4 O, vec2 u) {
    for ( vec2 R = iResolution.xy, U = abs( (u+u-R) / R.y );
          O.w++<9. && length(U)<1. ;
          O += .12
        ) U = abs(U/.4-1.); }
