const float PI = 3.14159265358979323846264;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec2 pos = uv;
    pos.y -= 0.05;

    float j = 0.2;

    pos /= max(max(sin(iTime * 2.) - 0.3, j+0.2), cos(iTime * 2. + 0.4));

    pos.y -= 0.2;
    float toCenter = length(pos) * 6.0;

    // float angle = abs(atan(pos.x, pos.y) / PI);

    float angle = (atan(pos.y, pos.x));

    // float heart = (13.0 * angle - 22.0 * angle * angle + 10.0 * pow(angle, 3.0)) / (7.0 - 6.0 * angle); 

    float heart = (sin(angle) * sqrt(abs(cos(angle)))) / (sin(angle) + 7.0 / 5.0) - 2.0 * sin(angle) + 2.0;

    vec3 color1 = vec3(1.000, 0.235, 0.176);
    vec3 color2 = vec3(1.000, 0.603, 0.642);

    // vec3 color1 = vec3(1.0);
    // vec3 color2 = vec3(0.0);

    heart =  smoothstep(heart, heart - 0.2, toCenter);

    vec3 color =  mix(color1, color2, smoothstep(0.1, -0.1, heart));


    fragColor = vec4(color,1.0);
}
