float ripple( vec2 p, vec2 x, float r, float time, float speed )
{
	float d = distance(p,x) * 10.0 - time * speed;
	float a = mod(floor(d),2.0);
	return a;
}

float pattern( vec2 x, float r, float time, float speed1, float speed2 )
{
	x /= r;
	vec2 xy = floor(x);
	float k = smoothstep( 0.45, 0.55, 1.0 - length(fract(x) - 0.5) );

	float i = xy.x + xy.y * 4.0;
	float t = max( time * speed1 - i, 0.0 );
	float phase = max( 0.0, sin( t * speed2 ) * k );

	return phase;
}

float tri( float d )
{
	float r1 = 0.5;
	return max(r1 - d, 0.0) / r1;
}

float selector( float T, float N )
{
	return mod( floor(iTime / T), N );
}


vec2 grid( vec2 i )
{
	vec2 center = vec2( 0, selector(3.0,4.0) / 2.0 - 1.0 );
	vec2 b = abs(i-center);
	float A = 2.0;
	float B = 2.0;
	vec2 z = i * (selector(2.5,4.0) * 5.0 + 10.0 ) / ( 1.0 + A * b.x * b.x + B * b.y * b.y );
	return z / (fract(iTime) * selector(5.0,2.0) + 1.0);
}

float vignette( vec2 uv )
{
	return (1.0 - dot(uv,uv) * 2.0);
}

vec3 r_color()
{
	return mix( vec3(1,0,0), vec3(16.0 * 15.0 / 255.0, 160.0 / 255.0, 13.0 * 16.0 / 255.0 ), selector(2.25,2.0) );
}

vec3 w_color()
{
	return mix( vec3(1,1,1), vec3(0.2,0.2,0.2), selector(1.5,2.0) );
}

float left_to_right( vec2 xy ) 
{
	return smoothstep(0.1,0.35,-mod(floor(xy.x),8.0) + mod(iTime * 8.0,8.0));
}

float up_to_bottom( vec2 xy )
{
	return smoothstep(0.1,0.35,-mod(floor(-xy.y),8.0) + mod(iTime * 8.0,8.0));
}

float animator( vec2 xy )
{
	float z = selector(5.0,3.0);

	if (z == 0.0) {		
		return left_to_right(xy);
	} 
	else if (z == 1.0) {		
		return up_to_bottom(xy);
	}
	return 1.0;
}

float subpattern( vec2 gr )
{
	vec2 x = fract(gr);
	float d = 2.0 * length(x - 0.5);
	if (fract(iTime*0.25) < 0.25) {
		d /=  0.5 + fract(2.0 * iTime);
	}
	float e = tri(d);
	float f = 2.0 * abs(e - 0.5);
	float k = selector( 12.0, 3.0 );
	float ani = selector( 5.0, 2.0 );
	if (k == 0.0) {
		return smoothstep(0.49,0.51,fract(x.y)) * mix( 1.0, up_to_bottom(gr), ani );
	} 
	else if (k == 1.0) {
		return smoothstep(0.49,0.51,fract(x.x)) * mix( 1.0, left_to_right(gr), ani );
	}
	else {
		return smoothstep(0.1,0.5,tri(f)) * mix( 1.0, animator(gr), ani );
	}
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy/2.0) /iResolution.x;
	vec2 gr = iTime + grid(uv);
	float g = subpattern( gr );
	//g = smoothstep(0.95,1.0,fract(uv.x*10.0));

	vec4 rgba = vec4(mix(r_color(),w_color(),g),1);

	fragColor = rgba * vignette(uv);
}
