#define d distance
#define pi 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float r = sin(iTime*pi)/10.;
	vec2 u = fragCoord.xy / iResolution.xy;

	fragColor = vec4(vec3(1.,0.6,0.)-vec3(min(d(u, vec2(.4-r)),min(d(u, vec2(.6+r)),min(d(u, vec2(.6+r, .4-r)), d(u, vec2(.4-r,.6+r)))))),1.);
}
