void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = vec2(fragCoord.x / iResolution.x,
				  fragCoord.y / iResolution.y);


	vec4 color1 = vec4(sin(iTime)*sin(iTime)/2.0*0.855+0.4, sin(iTime)*sin(iTime)*0.0234, sin(iTime)*sin(iTime)*0.222,0.1);
	vec4 color2 = vec4(sin(iTime)*sin(iTime)*0.884, sin(iTime)*sin(iTime)/2.0*0.850+0.5, sin(iTime)*sin(iTime)*0.648,0.1);
	vec4 color3 = vec4(sin(iTime)*sin(iTime)*0.348, sin(iTime)*sin(iTime)*0.555, sin(iTime)*sin(iTime)/2.*0.641+0.6,0.1);
	vec4 pixel;

	if ( r.y*cos(2.5*iTime) < 1.0/3.0) {
		pixel = color1
            // Bleu
            ;
	} else if( r.y*cos(2.5*iTime) < 2.0/3.0 ) {
		pixel = color2;
        // Vert
	} else {
		pixel = color3;
        // Rouge
	}


	fragColor = vec4(pixel);
}
