bool isInsideCircle(vec2 uv, vec2 center, float radius)
{
	return distance(uv, center) < radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;

	float val = 8.0*sin(1.0*iTime)+16.0;

	vec2 p = floor(uv/val+0.5)*val;

	vec3 col = texture(iChannel0, p/iResolution.xy).rgb;	

	float gray = (col.r + col.g + col.b)/3.0;

	if( isInsideCircle(uv, p, (1.0-gray)*val*0.6))
	{	
	    fragColor = vec4(col.r, col.g, col.b, 1.0);
	}
	else
	{
		fragColor = vec4(1.0 , 1.0, 1.0, 1.0);
	}		
}
