float sdCylinder(vec3 p, vec2 h) {
    return max( length(p.xz)-h.x, abs(p.y)-h.y );
}

vec2 rot2(vec2 k, float t) {
    return vec2(cos(t)*k.x-sin(t)*k.y,sin(t)*k.x+cos(t)*k.y);
}
/***********************************************/
float hash(float n) { 
	return fract(sin(n)*43758.5453123); 
}

float noise2(vec2 x) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
	float n = p.x + p.y*57.0;
    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}

/***********************************************/
float smine(float a, float b, float k) {
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}
/***********************************************/
float body(vec3 p) {
    p.y-=0.05;
    vec3 q=p;
    /* main */
    p.z+=0.1;    
    p.y=p.y + sin(p.y*4.5)*0.3;
    p.x=p.x - sin(p.x*1.0)*0.4;
  float m=length(p)-0.55;
    /* back */
    p.z=q.z -0.4;
    p.y=q.y +0.10;
    p.x=q.x - sin(q.x*1.0)*0.4;
  float b=length(p)-0.18;
    return smine(m,b, 12.0);
}

float eyesticks(vec3 p) {
    p.z+=0.35+sin(p.y);
    p.y-=0.2;
    p.x-=0.2;
  float l=sdCylinder(p, vec2(0.03,0.02));
    p.x+=0.4;
  float r=sdCylinder(p, vec2(0.03,0.02));
    return min(l,r);
}

float eyes(vec3 p) {
    p.z+=0.57;
    p.y-=0.22;
    p.x-=0.2;
  float l=length(p)-0.03;
    p.x+=0.4;
  float r=length(p)-0.03;
    return min(l,r);
}

float clamps(vec3 p) {
    vec3 q=p;
    p.x-=1.8 + sin(p.z*1.1);
    p.z=p.z +0.7 + cos(p.z*1.5)*0.1 + sin(p.x)*0.2;
    p.y=p.y +0.2 + sin(p.y*2.5)*0.2;
  float b=length(p)-0.3;
    q.x-=0.7 - sin(q.z*0.7);
    q.z+=0.15 + sin(q.x)*0.2;
    q.y+=0.1 + sin(q.y*1.0) - sin(q.z*0.5);
  float t=length(q)-0.15;
    q.x-=0.15 ;
    q.z-=-0.2+  sin(q.z*0.5)*0.1;
    q.y+=0.0;
  float m=length(q)-0.2;

    return min(min(m,b),t);
}
